CREATE TABLE review (
	uid BIGINT(20) PRIMARY KEY NOT NULL,
	review LONGTEXT NOT NULL,
  veredict LONGTEXT,
  evaluation BIGINT(20),  
  views BIGINT(20) DEFAULT 0,
  publication_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  isbn_book VARCHAR(50) NOT NULL,
  FOREIGN KEY (isbn_book) REFERENCES book(isbn)  
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


INSERT INTO review (uid, review, veredict, evaluation, views, isbn_book) 
  VALUES (1,'O Livro conta a história de Shadow Moon, um detento que ao fim de sua sentença após 3 longos anos na cadeia, só espera voltar para sua casa em Eagle point com sua esposa Laura. Mas o que Shadow não imagina é que seu destino está traçado em um rumo muito diferente.\n\nApós um acidente de carro um dia antes de ser solto, sua esposa morre, e então Shadow frustrado e desorientado em um turbilhão de sentimentos conhece um personagem emblemático e misterioso chamado Wednesday, que o recruta com o propósito de reunir os Deuses antigos, tratados no livro como divindades vivendo de maneira decadente.\n\nAo longo da história Shadow se mostra mais importante do que pensa e por vezes tem sonhos enigmáticos e tenebrosos que envolve sua esposa e criaturas mitológicas que mais tarde acabam se mostrando mais do que simples sonhos sem consequências.\nEntão com a aproximação da grande “tempestade” uma iminente batalha está prestes a ser travada entre os deuses antigos e os novos, cultuados nos dias de hoje tais como: deuses da informação, televisão, moda e etc…\n\nCom algumas histórias paralelas fica meio confuso e complicado entender algumas partes e há também alguns personagens importantes que poderiam ter tido um desenvolvimento mais amplo.','Mostrando uma leitura cansativa e confusa em determinados momentos, o livro não chega a empolgar, e fica andando numa linha tênue. A escrita de Gaiman é ótima, entretanto seu desenvolvimento neste livro ficou aquém do esperado. Deuses Americanos é sem dúv',5,109,'9788551000724');
INSERT INTO review (uid, review, veredict, evaluation, views, isbn_book) 
  VALUES (2,'A escuridão não é apenas a ausência da luz. Ela possui várias faces. Pode ser a sombra de um coração dilacerado pela dor da separação e arrependimento. Talvez o apagão sobre uma mente horrorizada frente ao sangue e a morte iminente. “Abominação” flerta com várias apresentações do lado escuro da vida, nos revelando uma história de caça, fuga, reencontros e corações partidos.\n\nDurante a era conhecida como medieval, os nórdicos incansavelmente invadiram e conquistaram parte da Europa. Neste cenário caótico, o reino de Wessex de Alfredo o Grande, se manteve de pé como o único a resistir ás investidas vindas do gelado norte. Mas sua residência está diminuindo. Frente ao medo e desespero um corrompido arcebispo está disposto a desvendar os segredos contidos em velhos pergaminhos que prometem a capacidade de transformar homens em bestas-feras, gerando assim um exército invencível para Alfredo. Mas como não há maneira de flertar com o mal sem se contaminar, o poder da escuridão torna essa empreitada descontrolada, cabendo a Alfredo, outrora o maior beneficiado, impedir que a morte se espalhe para a Europa. Ele resolve convocar o bravo sir Wulfric e seus guerreiros para tal missão.\n\nEm meio há anos de batalhas e perseguições foi criada uma “Ordem” com o objetivo de combater essas “abominações”. Do meio deste grupo surge a jovem Indra e seu inseparável falcão Venator. Em busca de criaturas e da aprovação do seu pai ela parte em uma jornada mortal por terras inóspitas onde acabará encontrando a face do mal não necessariamente em uma criatura, mas em alguém semelhante a ela.','Essa obra é a prova que não existe “receita de bolo” na literatura. Não há garantia que juntando elementos “certos e apelativos” teremos um resultado positivo. Primeiro não há vikings no livro. Esqueça a propaganda da editora. Não venha atrás de um Ragnar',5,82,'9788566636796');
INSERT INTO review (uid, review, veredict, evaluation, views, isbn_book) 
  VALUES (3,'Revival já entrega muito de sua narrativa através do título. Com todas as suas influências e referências, de Mary Shelley que segundo King foi uma das pessoas que o ajudaram a construir sua “casa”, ao lado de HP Lovecraft e Arthur Machen, cujo O Grande Deus Pan “tem me assombrado toda a minha vida”. Ao longo do livro, Ray Bradbury também é homenageado. Mas é Lovecraft, e a frase “Aquilo que não está morto pode jazer eternamente e com eras estranhas, até a morte pode morrer “, que reverberam em todo o livro.\n\nRevival conta a história de Jamie Morton, desde sua tenra idade até sua fase adulta onde tornou-se um guitarrista de qualidade razoável e ex-dependente químico, do reverendo Charles Jacobs e tudo que os cercou ao longo dos anos. Narrado peremptoriamente em primeira pessoa pelo próprio Jamie, acompanhamos o desenrolar de uma história que mistura suspense e terror, com um dos elementos mais marcantes de Stephen King: seus personagens e seu desenvolvimento.\n\nJamie tem seis anos quando conhece o jovem ministro metodista Charles Jacobs. Apaixonado por suas responsabilidades, o novo ministro revigora sua congregação com seu entusiasmo, e deslumbra seu grupo de jovens com demonstrações de eletricidade em ação. Mas quando a pequena família de Jacobs é atingida por uma tragédia terrível e seu pastor se vê perdido num mar revolto de descrença e blasfêmia, os alicerces da pequena congregação e todos os seus envolvidos são minados com a desconfiança. Quando, anos mais tarde, Jamie o encontra novamente, Jacobs está diferente, mas ainda extremamente obcecado com o poder da eletricidade, e seus experimentos – sua vontade de experimentar assuntos desconhecidos.\n\nPoucos escritores conseguem tão facilmente, tão naturalmente e tão intimamente descrever os detalhes de uma vida. Talvez por isso, quando o livro começa a deslizar para longe de seu proposto, estamos felizes e enredados em seguir com Jamie. Nós fomos enganados, assim como nosso narrador, em esquecer exatamente que tipo de livro este prometeu ser. Revival em grande parte de sua narrativa não é um livro de terror, não vamos ficar temerosos ao que encontrar através de uma porta entreaberta num quarto escuro, não. Revival é um estudo da mente humana frente às suas frustrações e desespero. É a trilha que devemos seguir, cada vez que somos atingidos como por um raio pelas decepções que a vida nos traz. O grande destaque do livro é sem dúvidas o seu final e o que ele traz consigo, que segundo o próprio autor foi um dos seus mais perturbadores já escritos. O final que o deixou em um estado de puro terror frente ao desconhecido. E é exatamente assim que ficamos, temerosos. King conseguiu com sua maestria característica nos conduzir a um cenário do mais puro poder de impotência frente a nossa grande dúvida, o que vem a seguir?','Revival é King em uma de suas melhores performances ultimamente, incisivo e perturbador. O desenvolvimento dos personagens já é uma de suas marcas registradas e aqui não é diferente. Boa parte do livro vamos acompanhar Jamie em suas dúvidas e frustrações ',5,68,'9788581053103');
INSERT INTO review (uid, review, veredict, evaluation, views, isbn_book) 
  VALUES (4,'Zumbis é um termo recorrente nos dias de hoje, filmes, séries, games e livros são abastecidos regularmente com o gênero. E em como toda grande emancipação ou exploração de terrenos já preenchidos, existe uma polarização enorme quanto à qualidade de suas respectivas obras. Os consumidores, sejam eles, cinéfilos, gamers ou leitores são exigentes, e não poderia ser diferente, nós como raça buscamos sempre o melhor e não nos contentamos com o pouco, está na essência humana. Sendo assim, você como autor iniciante, arriscar-se pelas veredas de um gênero já consolidado é de fato um passo bastante arriscado. A questão é: você acredita que possui uma boa história a ser contada? A certeza é nebulosa, requer avaliações externas, mas o fato de você como autor crer no fruto do seu trabalho, é o primeiro passo para materializar a obra e procurar a fidedignidade de sua criação.\n\nFebre Vermelha escrito pelo autor brasileiro Francis Graciotto e lançado neste ano de 2016 é um livro de terror com uma pegada mais realista. O cenário se resume a Santos, por onde a praga se inicia, indo até alguns pontos da cidade de São Paulo. A história é contada através de vários protagonistas, como num filme os núcleos são trabalhados alternadamente, proporcionando uma dinâmica para a narrativa.\n\nEm Santos às vésperas do fim do ano, comumente as pessoas viajam ao litoral em busca de paz e confraternização. Quando incidentes ocorrem em alguns pontos da cidade, o caos está instalado. Pessoas agindo de maneira agressiva e canibalesca, atacando outros semelhantes sem hesitação. Neste cenário de insegurança e medo, alianças um tanto quanto improváveis precisam serem feitas, não há mais classificação de gêneros ou classe econômica, todos são vítimas da incerteza do amanhã e precisam trabalharem juntas para alcançarem a sobrevivência. Gilbertoé um professor casado com Monique e pai de Maria Rita, juntos eles pretendem ter um raro tempo para família em Santos. Jorge, um lutador de Muay Thai e seu amigo Carlinhos levam uma vida indecente e pretendem fazer um último “trabalho” antes do encerramento do ano. Gustavoé um estudante que aproveita a viagem dos pais à Europa, para reunir os amigos em casa e se esbaldar nas bebidas e comemorações. Pietro, um ex-militar do exército com uma missão a ser cumprida. André, um pastor Pai de duas crianças e casado com Leticia, vítima de câncer. Formam o grupo de sobreviventes.\n\nO livro é repleto de ação e terror. É notável a habilidade e o cuidado com que o autor teve ao dar relevância a todos os capítulos. Todos, sem exceção possui sua carga dramática e seus respectivos momentos de tensão. O ritmo é frenético, o virar de páginas é uma constante na ânsia de saber o que vem a seguir. O livro é dividido em 12 capítulos e 1 prólogo, todos enunciados pelo índice. A escrita é bem acessível e objetiva. O terror é brutal, sanguinário e realista. Os “zumbis” ou vítimas da Febre Vermelha, são em grande parte muito mais rápidos e espertos que os famigerados mortos-vivos de outros livros ou filmes. O interessante é a maneira com a qual trabalham em conjunto em determinados momentos, agindo como uma alcateia de lobos, em busca da caça.\n\nUm dos pontos negativos do livro é a separação entre POV’s (pontos de vista) dos personagens. Num mesmo capítulo é natural ocorrer esta alternância, entretanto, não há nada que a indique, ela ocorre de maneira abrupta, o que causa em determinados momentos uma quebra de ritmo desnecessária. A edição está bem bacana, não me lembro de ter encontrado erros gramaticais ou de pontuação.','Febre Vermelha é um ótimo livro de terror nacional, mesmo que em seu cerne tenha um tema batido, o autor Francis conseguiu trazer um ambiente mais realista e explorou as reações e consequências trazidas pelo suposto vírus em pessoas comuns. Com muita ação',5,20,'9788565269377');
INSERT INTO review (uid, review, veredict, evaluation, views, isbn_book) 
  VALUES (5,'A Canção dos Shenlongs: Guerras Épicas do Império de Housai é o primeiro volume de uma saga de Fantasia (voltado à cultura Oriental) escrito pelo autor brasileiro Diogo Andrade lançado em 2016 exclusivamente em formato digital (E-book).\n\nNo livro acompanhamos o ponto de vista por parte de Mu, Shenlong de Shanjin, entenda o termo Shenlongcomo algo muito próximo de um monge, toda a cultura apresentada ao longo da obra, lembra bastante a esta classe. Mu descreve para alguém (?) os eventos que culminaram na expulsão de seu “irmão” Rukdo Templo da Montanha e tudo o que ocorreu a partir daí.\n\nShanjin é um dos 4 Templos dos Shenlongs – ministrado pelo Ababe Kame, sub comandado pelos poderosos guerreiros Sarujin e Shizu e que conta ainda com os conselhos da misteriosa Velha Gilga. O Templo da Montanha é um lugar pacifico e harmonioso, onde a cultura dos “monges” são passadas aos mais jovens através das aulas, e todos os ensinamentos dentre estes, a incrível habilidade de artes marciaissão assimilados de maneira plácida. Pouco se sabe sobre o mundo “externo”, sua interferência sobre os Shenlongs é praticamente nula, visto que o templo é protegido por uma barreira mágica, instransponível para os forasteiros. Porém, quando uma ameaça surge, os guerreiros terão de se preparar para enfrenta-la e caso não tenham sucesso, o preço a ser pago pode ser alto demais.\n\nA Canção dos Shenlongs é daqueles livros que ao terminar, você anseia por mais, anseia por descobrir mais sobre o mundo e a cultura ali apresentados, sobre os personagens, seu passado e o caminho para onde o futuro aponta, anseia por mais descrições sobre todo o contexto envolvido. Em suma, A Canção dos Shenlongs é realmente como uma doce melodia dentro de um compilado de outras músicas, daquelas que te faz buscar os outros trabalhos do álbum, afim de apreciar toda a obra criada.\n\nO livro ou a novela (devido ao tamanho da obra), é de uma leitura ágil e extremamente fluída, me lembro de ter finalizado em um único dia tranquilamente. A escrita do autor é excelente, as descrições das batalhas são bem trabalhadase o ambiente possui uma imersão considerável. O cenário é restrito ao Templo da Montanha Shanjin e sua floresta ao redor, mas isso de forma nenhuma limita a experiência do leitor. Obra recomendadíssima! Ideal para aqueles intervalos entre obras mais complexas com suas milhares de páginas, escutem a A Canção dos Shenlongs, e conheçam mais sobre este mundo fantástico.',NULL,5,37,'B01M0D1XT6');
INSERT INTO review (uid, review, veredict, evaluation, views, isbn_book) 
  VALUES (6,'Presságios de Guerra é o primeiro livro da saga Corações nas Sombras do autor brasileiro Allan Francis que têm já planejado outras 4 continuações, publicado em setembro deste ano pela editora portuguesa Chiado. A obra nos transporta para o mundo de Ifianor, local de magias, intrigas, guerra e criaturas místicas, tais como elfos, anões, orcs, demônios, dragões, goblins, entre outros.\nIfianor é dividido em dois continentes: Primas e Pallas, que por sua vez abrange diversos reinos, entre eles o reino de Damatia, cuja figura governante recai sobre os ombros de sua rainha e seus exércitos, estes que são compostos pelas guerreiras do reino, em Damatia os homens não governam e tão pouco participam das principais decisões. Temos Lessália, reino dos elfos governado pelo orgulhoso e arrogante Tirdan, que acorrentado nas entranhas de seu ego, comum aos elfos governantes, mantém uma distância inapropriada aos outros reinos, tratando-os sempre com indiferença e inferioridade. Temos o isolado reino de Draconia, lar dos dragões comandado pelo poderoso Elegibur. E Dung-Dhar reino dos anões do norte, cuja liderança recai sobre o martelo poderoso e o orgulho feroz do assim considerado Deus por seu povo, Morghom. Há diversos outros reinos e cidades importantes, entre elas Abak, que será palco de um dos principais núcleos da história. Ao todo o mundo de ifianor é composto por 18 reinos e alguns outros pequenos reinos e principados.\nIfianor é um mundo complexo com suas diversidades de raças e culturas, para amenizar e tentar controlar os ânimos dos reinos mais exaltados foi criado o Alto Conselho, que por sua vez obteve autonomia em suas decisões a partir dos tratados de reinos e raças, assinados por seus respectivos governantes há aproximadamente 200 anos, quando acabou o que ficou conhecido como as Guerras Seculares e os orcs, demônios e grande parte das ameaças foram levados para Agonia e presos sob um poderoso selo de magia. O Alto Conselho é composto por várias cadeias de responsabilidades e decisões, todos os reinos são integrantes deste comitê, que têm sua liderança figurada no poderoso elfo Cirdan.\nNossa história começa quando misteriosos atentados são realizados em diferentes partes do mundo, sejam eles proferidos na ilha de Sisam, onde um exército de orcs atacou a cidade e matou várias pessoas ou no castelo de Dankar, lar de um dos seis talismãs detentores de poderes extraordinários e guardado pela poderosa maga Selene. Outro ataque ocorreu nas montanhas de mercúrio onde um dragão negro de aspecto mortal aterrorizou e ceifou durante um ritual religioso. A partir daí temos as consequências destes ataques e a sombra de uma guerra pairando sobre os céus de Ifianor.\nAcompanhamos o livro através de diversos pontos de vista, são vários personagens de variadas partes do mundo e com objetivos diferentes. Todos eles são muito bem trabalhados, e ao que à primeira vista pode denotar uma conotação simples e rasa na construção dos personagens, ao longo do livro é interessante acompanhar esta evolução, sublinhando as mudanças em suas personalidades e no modo de agir. Seja ele o forte guerreiro que ao longo do livro vai definhando em suas desconfianças ou na pobre moça que se sente insegura e com o decorrer dos eventos se torna um símbolo de poder e esperança.\nO livro detém uma escrita bem simples e direta, por mais que seja um livro grande, com pouco mais de 730 páginas, não há prolixidade em demasia por parte do autor. Na obra encontramos bastante cenas de violência e algumas passagens com clara conotação sexual. A primeira parte do livro não me agradou muito, seja pela narrativa inicial ou pelas descrições das primeiras batalhas, que não me prenderam como esperava. Entretanto, há de se notar a evolução muito clara da história, principalmente a partir da segunda metade do livro. Todo o amarrar do enredo e o rumo a que ela se deu me cativou instantaneamente, as batalhas melhoraram consideravelmente assim como a clara e evidente evolução da narrativa, todos os personagens tiveram seus momentos no livro, naturalmente por haver diversos, sempre há aquele a qual você não simpatiza, da mesma forma que há outros que lhe farão acompanhar detidamente sua trajetória.\nAcredito que esta é a proposta da obra, ser mais minuciosa na primeira metade e mais explosiva e voraz na segunda. Mesmo sendo um livro grande que se não fosse por sua letra pequena, facilmente passaria a barreira das 1000 páginas, não me senti fadigado e cansado em nenhum momento. Os personagens te prendem na primeira metade, e a história, a tensão, as intrigas e as descobertas são o grande trunfo da segunda. O final é muito bom, beirando ao espetacular, amarrando muito bem as pontas soltas e criando outras novas ainda mais empolgantes.\nUm ponto negativo são os erros de ortografia e alguns de diagramação e formatação encontrados ao longo do livro. O trabalho de revisão não foi apropriado e em contato com o autor ele se mostrou bastante descontente com estes elementos e já está providenciando suas efetivas correções.','Em suma Corações nas Sombras é um ótimo livro de literatura fantástica nacional, com uma história complexa e personagens muito bem construídos ao longo de um mundo gigantesco. É indicado para todos os amantes de Fantasia e até mesmo para aqueles que nunca',5,55,'9789895170463');
INSERT INTO review (uid, review, veredict, evaluation, views, isbn_book) 
  VALUES (7,'“Mal sabiam os rapazes que, naquele momento, com aquelas palavras, a estranha mulher não captada na câmera havia dado o melhor de todos os conselhos.\nInfelizmente eles não deram ouvidos. “\nDiário de um Exorcista, escrito por Renato Siqueira e Luciano Milici e lançado pela editora Évora/Generale em 2013, é um livro de terror nacional baseado em fatos reais que relata a vida e obra de um dos maiores exorcistas da América Latina. Para preservar a identidade e a privacidade dos envolvidos, todos os nomes contidos na narrativa são fictícios.\nO livro é descrito em sua maior parte pelo padre Lucas Vidal através da narrativa em primeira pessoa, no início do livro acompanhamos dois jovens rapazes que em busca de um trabalho da faculdade, solicitam uma entrevista junto ao famoso exorcista. O tema obviamente seria o relato preciso de toda sua trajetória, passando pelas provações da infância, contendo tragédias familiares e visões demoníacas, até o ponto mais contundente de sua carreira.\n\n“Mais vale ao diabo desviar de um homem de fé que carregar a alma de mil ímpios.“\nA primeira metade do livro é rodeada de suspense, tensão e algumas boas reviravoltas, e é justamente onde se reside o melhor da obra. A parte descrita como a infância do Padre Vidal é absolutamente intrigante, a forma como o até então garoto vê o desenvolvimento do caos sendo estabelecido em sua família por uma presença misteriosa e demoníaca é de uma contundência pontual, você como leitor se sente fisgado para os temores daquela criança ao andar no meio da noite seguindo seu pai por uma rua escura, cercado por sombras e mistérios, ou pelo medo crescente se enraizando nas sombras daquela família simples e comum. O sobrenatural estava ali à espreita, não de forma fantasiosa como em muitas outras obras, mas de maneira praticamente natural.\n\n“Passei a ver pessoas sendo acompanhadas por seres espirituais malignos o tempo todo. Muitas não conseguiam ver esses seres, outras, porém, conviviam com eles como se fossem conhecidos. “\n\nO adendo negativo fica por parte da segunda metade do livro, mais próximo do fim todo o clima soturno e de apreensão desenvolvido foi se rompendo em soluções um tanto quanto fantasiosas demais, de maneira forçosa e tão inatural que não condiz com a ótima primeira metade da obra. Há ainda boas reviravoltas e o desfecho é bastante interessante, mas em certo ponto da narrativa, o livro “peca” por arremeter a crendices que poderia muito bem ficar delineadas no subconscientedo leitor e não exposto lucidamente como de fato ocorre.\n\n“O mal me rondava novamente e isso, sim, era muito presente e palpável. Os pesadelos diziam isso e, pior, um estranho calafrio me acompanhava. Algo muito mais assustador e mortal do que eu havia presenciado em minha infância estava para ocorrer e os pesadelos eram apenas os arautos avisando que o exército das trevas estava chegando e era invencível. “\nEm suma, o Diário de um Exorcista é um bom livro de terror, com alguns relatos assustadores de expurgação demoníaca, vide exorcismos. Que irá satisfazer a todos que procuram algo sobre o assunto, ao final fica a sensação de que poderia ter sido melhor trabalhado, principalmente a partir da segunda metade do livro e se tornado uma das grandes referências do gênero.\n\nVale ressaltar que há filme nacional baseado neste livro chamado: “Diário de um Exorcista: Zero”. A edição do livro está belíssima, contendo imagens do filme em alta definição e até mesmo um CD com trailer vêm anexado a obra.',NULL,5,33,'9788563993724');
